    private void validarDatosUsuario(){
        if("Admin".equals(nombreUsuario.getText())){
            JOptionPane.showMessageDialog(this,"Nombre de Usuario inv치lido");
        }
        else{
            if(!contraseniaUsuario.getText().equals(contraseniaRepetida.getText())){
                JOptionPane.showMessageDialog(this,"Las contrase침as ingresadas no son iguales");
            }
            else{
                JOptionPane.showMessageDialog(this,"Usuario creado exitosamente");
            }
        }
    }
